import React, { useState } from "react";
import { validateField, isValidForm } from "../utils/validateApplication";
import { usStates, countries } from "../utils/stateCountryCodes";


const API_URL = "http://localhost:5000/submit-application";

const Application = () => {
    const initialFormState = {
        name_first: "",
        name_last: "",
        address_line_1: "",
        address_line_2: "",
        address_city: "",
        address_state: "",
        address_postal_code: "",
        address_country: "",
        document_ssn: "",
        email_address: "",
        birth_date: ""
    };
    const [formData, setFormData] = useState(initialFormState);

    const [errors, setErrors] = useState({});
    const [message, setMessage] = useState("");

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });

        const error = validateField(name, value);
        setErrors({ ...errors, [name]: error });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!isValidForm(formData, errors)) {
            setMessage("Please fill in all required fields correctly.");
            return;
        }

        try {
            const response = await fetch(API_URL, {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify(formData)
            });

            const data = await response.json();
            if (response.ok) {
                setMessage("✅ Application submitted successfully!");
                setFormData(initialFormState);
            } else {
                setMessage(`❌ Error: ${data.error || "Failed to submit application"}`);
            }
        } catch (error) {
            setMessage("❌ Error submitting application. Please try again.");
        }
    };

    return (
        <div>
            <h2>Application Form</h2>
            <form onSubmit={handleSubmit}>
                <label>First Name*:</label>
                <input type="text" name="name_first" value={formData.name_first} onChange={handleChange} required />
                {errors.name_first && <p className="error">{errors.name_first}</p>}

                <label>Last Name*:</label>
                <input type="text" name="name_last" value={formData.name_last} onChange={handleChange} required />
                {errors.name_last && <p className="error">{errors.name_last}</p>}

                <label>Address Line 1*:</label>
                <input type="text" name="address_line_1" value={formData.address_line_1} onChange={handleChange} required />
                {errors.address_line_1 && <p className="error">{errors.address_line_1}</p>}

                <label>Address Line 2 (Optional):</label>
                <input type="text" name="address_line_2" value={formData.address_line_2} onChange={handleChange} />

                <label>City*:</label>
                <input type="text" name="address_city" value={formData.address_city} onChange={handleChange} required />
                {errors.address_city && <p className="error">{errors.address_city}</p>}

                <label>State*:</label>
                <select name="address_state" value={formData.address_state} onChange={handleChange} required>
                    <option value="" disabled>Select a State</option>
                    {usStates.map((state) => (
                        <option key={state.code} value={state.code}>
                            {state.name} ({state.code})
                        </option>
                    ))}
                </select>
                {errors.address_state && <p className="error">{errors.address_state}</p>}

                <label>ZIP/Postal Code*:</label>
                <input type="text" name="address_postal_code" value={formData.address_postal_code} onChange={handleChange} required />
                {errors.address_postal_code && <p className="error">{errors.address_postal_code}</p>}

                <label>Country*:</label>
                <select name="address_country" value={formData.address_country} onChange={handleChange} required>
                    <option value="" disabled>Select a Country</option>
                    {countries.map((country) => (
                        <option key={country.code} value={country.code}>
                            {country.name} ({country.code})
                        </option>
                    ))}
                </select>

                <label>SSN (9 digits, no dashes)*:</label>
                <input type="text" name="document_ssn" value={formData.document_ssn} onChange={handleChange} required maxLength="9" />
                {errors.document_ssn && <p className="error">{errors.document_ssn}</p>}

                <label>Email Address*:</label>
                <input type="email" name="email_address" value={formData.email_address} onChange={handleChange} required />
                {errors.email_address && <p className="error">{errors.email_address}</p>}

                <label>Date of Birth*:</label>
                <input type="date" name="birth_date" value={formData.birth_date} onChange={handleChange} required />
                {errors.birth_date && <p className="error">{errors.birth_date}</p>}

                <button type="submit">Submit</button>
            </form>

            {message && <p className={message.startsWith("❌") ? "error-message" : "success-message"}>{message}</p>}
        </div>
    );
};

export default Application;
