export const validateField = (name, value) => {
  let error = "";

  switch (name) {
      case "email_address":
          error = /\S+@\S+\.\S+/.test(value) ? "" : "Invalid email format";
          break;
      case "address_state":
          error = /^[A-Z]{2}$/.test(value) ? "" : "State must be a valid two-letter code (e.g. NY)";
          break;
      case "address_postal_code":
          error = /^\d{5}(-\d{4})?$/.test(value) ? "" : "Invalid ZIP/Postal Code";
          break;
      case "document_ssn":
          error = /^\d{9}$/.test(value) ? "" : "SSN must be 9 digits (no dashes)";
          break;
      case "birth_date":
          error = /^\d{4}-\d{2}-\d{2}$/.test(value) ? "" : "Date must be in YYYY-MM-DD format";
          break;
      default:
          break;
  }

  return error;
};

export const isValidForm = (formData, errors) => {
  const requiredFields = ["name_first", "name_last", "address_line_1", "address_city", "address_state", "address_postal_code", "document_ssn", "email_address", "birth_date"];

  for (let field of requiredFields) {
      if (!formData[field]) return false;
  }

  return Object.values(errors).every((error) => !error);
};
