import React, { useState } from 'react';

const FORM_URL = 'http://localhost:5000/submit-application';

const Application = () => {
    const [formData, setFormData] = useState({ name: '', email: '', amount: '' });
    const [message, setMessage] = useState('');

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setMessage('Submitting...');

        try {
            const response = await fetch(FORM_URL, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(formData),
            });

            const data = await response.json();
            if (response.ok) {
                setMessage(`Success: ${JSON.stringify(data)}`);
                setFormData({ name: '', email: '', amount: '' });
            } else {
                setMessage(`Error: ${data.error || 'Failed to submit application'}`);
            }
        } catch (error) {
            setMessage('Error submitting application');
        }
    };

    return (
        <div>
            <h2>Application Form</h2>
            <form onSubmit={handleSubmit}>
                <label>Name:</label>
                <input type="text" name="name" value={formData.name} onChange={handleChange} required />

                <label>Email:</label>
                <input type="email" name="email" value={formData.email} onChange={handleChange} required />

                <label>Amount:</label>
                <input type="number" name="amount" value={formData.amount} onChange={handleChange} required />

                <button type="submit">Submit</button>
            </form>
            {message && <p>{message}</p>}
        </div>
    );
};

export default Application;
